import styles from "./AddUser.module.css";
import Button from "../UI/Button/Button";
import Card from "../UI/Card/Card";

import ErrorModal from "../UI/ErrorModal/ErrorModal";

import { useState } from "react";

const AddUser = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [errorContent, setErrorContent] = useState();

  const usernameChangeHandler = (e) => {
    setEnteredUsername(e.target.value);
  };

  const ageChangeHandler = (e) => {
    setEnteredAge(e.target.value);
  };

  const SubmitUserHandler = (e) => {
    e.preventDefault();

    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      console.log("case1");
      setErrorContent(
        <ErrorModal
          title="Invalid Input"
          message="Please enter a valid name and age (non-empty values"
        />
      );
      return;
    } else if (+enteredAge < 1) {
      setErrorContent(
        <ErrorModal title="Error" message="enter positive age" />
      );
      console.log("case2");
      return;
    }

    props.onClick(enteredUsername, enteredAge);
    setEnteredUsername("");
    setEnteredAge("");
    // document.querySelectorAll("input").forEach((input) => (input.value = "")); //Resetting logic 1
  };

  return (
    <Card className={styles.input}>
      {/* for customises html tags, className is not defined, it will works as prop and we need to acept this prop in Card component file and there we need to add it to className */}
      <form onSubmit={SubmitUserHandler}>
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          value={enteredUsername}
          onChange={usernameChangeHandler} //resetting logic 2
        ></input>
        <label htmlFor="age">Age (Years)</label>
        <input
          type="number"
          id="age"
          value={enteredAge}
          onChange={ageChangeHandler}
        ></input>
        <Button type="submit">Add User</Button>
      </form>
      {errorContent}
    </Card>
  );
};
export default AddUser;
