import React, { useState } from "react";
import AddUser from "./AddUser/AddUser";
import Model1 from "./Model/Model1";

import Model2 from "./Model/Model2";
import DisplayUser from "./DisplayUser/DisplayUser";

function App() {
  const [userDetails, setUserDetails] = useState([]);
  const [content, setContent] = useState(<></>);

  const addUserHandler = (enteredUsername, enteredAge) => {
    if (!enteredUsername.trim() || !enteredAge.trim()) {
      console.log("case1");
      props.onDisplayNext(1);
    } else if (enteredAge < 0) {
      props.onDisplayNext(2);
      console.log("case2");
    } else {
      console.log("case3");
      props.onDisplayNext(3);
    }

    setUserDetails((prevDetails) => {
      const updateDetails = [...prevDetails];
      updateDetails.unshift([enteredUsername, enteredAge]);
      return updateDetails;
    });
  };

  const displayNextHandler = (nextContent) => {
    if (nextContent === 1) {
      console.log("case1-1");
      setContent(<Model1></Model1>);
    } else if (nextContent === 2) {
      console.log("case2-1");
      setContent(<Model2></Model2>);
    } else {
      console.log(userDetails);
      setContent(<DisplayUser items={userDetails}></DisplayUser>);
    }
  };

  return (
    <div>
      <section id="upper-section">
        <AddUser
          onClick={addUserHandler}
          // onDisplayNext={displayNextHandler}
        ></AddUser>
      </section>
      {content}
    </div>
  );
}

export default App;
